package javaBasic;

public class Ejemplo_Ciclo_For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int y=10;
		
		//el buble for comienza cuando x=0 y x es una variable dentro del ciclo for
		// corre hasta que es es igual o menos que y
		for (int x=0; x<=y; x++) {
			
			System.out.println("El valor de x: " +x );
		}
		
	}

}
